//src/components/About/index.js

import React from 'react';
import {Redirect,Link} from 'react-router-dom';
import './Register.css';

class Register  extends React.Component{

constructor(props){
	super(props);
	this.state = {
	files:[],
	username:"",
	useravatar:"",
	password:"",
	redirect:false,
        duplicate:false,
	friends:[],
	};

	this.handleLogin = this.handleLogin.bind(this);


}

handleLogin(e){
  e.preventDefault();
  let duplicate_user = false;
  console.log("Entered login handle")
  const data = new FormData();
   data.append('userimg', this.state.files[0]);
   data.append('username',this.state.username);
   data.append('password',this.state.password);
   data.append('friends',this.state.friends);
 fetch("/login/add",
 {
	method:"POST",
	body:data,
 }
)
.then((res)=>{
 console.log(res.status);
if(res.status===500){
	console.log("Internal server error")
}
})
.catch(err=>console.log(err))

fetch("/login/"+this.state.username,{

	method:"GET",
	headers:{'Content-Type':'application/json'}
}
)
.then(res=>res.json())
.then((user)=>{
console.log(user);
if(user.length!==0){
	if(user[0].username.equals(this.state.username)){
		duplicate_user = true;
	}
}
}
)

console.log("The value of duplicate user is ");
console.log(duplicate_user)


 this.setState({redirect:true});

}

render(){

if(this.state.duplicate){

return(
<div>
	<h3>Given username already exists</h3><br/>
        <Link to="/Register">Go back to Registration page</Link>
</div>

);

}
else if(this.state.redirect){
 return(
	<Redirect push
            to="/"
        />

);
}

else{
return(
	<form onSubmit={this.handleLogin}>
      <label htmlFor="username">Username: </label>
      <input
        type="text"
        value={this.state.username}
        placeholder="enter a username"
        onChange={(e) => this.setState({username:e.target.value})}
      />
      <div>
        <label htmlFor="password">password: </label>
        <input
          type="password"
          value={this.state.password}
          placeholder="enter a password"
          onChange={(e) => this.setState({password:e.target.value})}
        />
      </div>
      <div>
	<label htmlFor="useravatar">Upload profile image :  </label>
	 <input type="file" name="userimg" onChange = {(e)=>{this.setState({files: e.target.files})}}/>
      </div>
      <button type="submit">Register</button>
    </form>
);
}


}
}

export default Register;
